/**
 * @说明 UDP连接客户端
 * @since
 */
public class UdpClient {
    public void startclient()
    {

        try {
            while(true){
                System.out.println("client");
                new Thread(new ClientImpl()).start();
                Thread.sleep(5000);
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
//        String str="3BE0D080";
//
//        float a= DataTypeExchange.hexToTenf(str);
//        System.out.println(a);
        try {
            Date date = new Date();
            Timestamp timeStamep = new Timestamp(date.getTime());
            System.out.println(timeStamep);
            while(true){
                System.out.println("client");
                new Thread(new ClientImpl()).start();
                Thread.sleep(1000);
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
